<?php
require_once __DIR__.'/PHPMailer/PHPMailerAutoload.php';
/** 
 * @author jguerrero
 * 
 */
class Correo {
    
    protected $fromName= 'Sistemas UTAGS';
    
    /**
     *
     * @var PHPMailer
     */
    public $mail;
    /**
     */
    function __construct() {
        $this->mail = new PHPMailer();
        $this->mail->isSMTP();
        $this->mail->CharSet = 'UTF-8';
        $this->mail->Encoding = 'base64';
        
        
        //Cargamos desde archivo de configuracion
        @require "/var/www/config/mail.notificacion.php";
        // Set mailer to use SMTP
        $this->mail->Host = $CONFIGURACION_PARAM_MAIL_NR_host;
        $this->mail->SMTPAuth = $CONFIGURACION_PARAM_MAIL_NR_SMTPAuth;
        $this->mail->Username = $CONFIGURACION_PARAM_MAIL_NR_username;
        $this->mail->Password = $CONFIGURACION_PARAM_MAIL_NR_password;
        //$this->mail->SMTPSecure = 'tls';
        $this->mail->Port = $CONFIGURACION_PARAM_MAIL_NR_port;
        $this->mail->From = $CONFIGURACION_PARAM_MAIL_NR_username;
        $this->mail->FromName = $this->fromName;
	}
	
	public function Enviar($para,$asunto,$mensaje){
		$mail=&$this->mail;
		$mail->clearAllRecipients();
		
		if(is_array($para) && count($para)>0){
		    foreach ($para as $destinatario){
		        $mail->addAddress($destinatario);
		    }
		}else{
		    $mail->addAddress($para);
		}
		
		$mail->Subject = $asunto;
		$mail->msgHTML($mensaje);
		
		return $mail->send();
	}
	

